import React, { useEffect, useState, useRef } from "react";
import defaultIcon from "../../assets/camera.svg";
import {
	UpdatePlaylistName, UpdateCurrentRound, UpdateScore, UpdateThumbnail, UpdateArtistOrSongTitle,
	UpdateArtistOrSong, UpdateRemainingGuesses, UpdateUserAnswerResult
} from "./GameInterfaceSubcomponents";

export default function GameInterface({ playlistYear, playlist, toggleGameComplete }) {
	// Reference for the text input box
	const userInput = useRef();

	// State handling for UI elements based on game progression
	const [userAnswerResult, setUserAnswerResult] = useState("");
	const [startNewRound, setStartNewRound] = useState(true);
	const [submitUserAnswerButtonDisabled, setSubmitUserAnswerButtonDisabled] = useState(false);
	const [startNewRoundButtonDisabled, setStartNewRoundButtonDisabled] = useState(true);
	const [song, setSong] = useState(null);
	const [score, setScore] = useState(0);
	const [currentRound, setCurrentRound] = useState(0);
	const [thumbnail, setThumbnail] = useState(defaultIcon);
	const [thumbnailBlur, setThumbnailBlur] = useState(30);
	const [artistOrSongTitle, setArtistOrSongTitle] = useState(null);
	const [artistOrSong, setArtistOrSong] = useState(null);
	const [remainingGuesses, setRemainingGuesses] = useState(3);

	var gameModePlaylist = playlist.tracks.items;

	// Set the attributes of the page to match the song
	// Executes on startup and when "Next Round" button is clicked
	useEffect(() => {
		try {
			// Setup UI and variables for a new round
			setStartNewRoundButtonDisabled(true);
			setRemainingGuesses(3);

			// Cycle through the entire playlist before ending the game
			if (currentRound < playlist.tracks.total) {
				getNewSong();
				setCurrentRound(currentRound => currentRound + 1);
				userInput.current.value = "";
			}
			else {
				toggleGameComplete(score);
				setScore(0);
			}
		}
		catch (error) {
			setUserAnswerResult("Sorry! There was an error.");
		}
	}, [startNewRound]);

	// Disable the game if the user runs out of guesses
	// Execute every time there is an update to remainingGuesses
	useEffect(() => {
		// Handle whether the guess was correct or not
		const isAnswerCorrect = isUserAnswerCorrect();
		if (isAnswerCorrect) correctAnswerRender();
		else setUserAnswerResult("Faux!");
		updateThumbnailBlur(isAnswerCorrect);

		// Update UI depending on the num of guesses left
		if (remainingGuesses === 3) {			// New round just started
			setUserAnswerResult("")
		}
		else if (remainingGuesses === 0) {		// User ran out of guesses = round over
			setUserAnswerResult("Jeu terminé!");
			setSubmitUserAnswerButtonDisabled(true);
			setStartNewRoundButtonDisabled(false);
		}

	}, [remainingGuesses]);

	// Retrieve a new random song from the playlist
	// Executes once during a new round
	const getNewSong = () => {
		try {
			var randomNumber = Math.floor(Math.random() * gameModePlaylist.length);	// Select a random song from the playlist by index
			var newSong = gameModePlaylist[randomNumber].track; 					// Get the song by index and save it

			setSong(newSong);											// Set the song's state
			updateArtistOrSong(Math.floor(Math.random() * 2)); 			// 0 = Guess the Artist && 1 = Guess the Song
			setSubmitUserAnswerButtonDisabled(false);
			setThumbnail(newSong.album.images[0].url);					// Change the img to the thumbnail
			updateThumbnailBlur();										// Reset thumbnail blur
			updateArtistOrSongTitle(newSong);							// Show the name of the artist/song
			setUserAnswerResult("");									// Reset results from user answer
			gameModePlaylist.splice(randomNumber, 1)					// Remove this song from the playlist
		}
		catch (error) {
			setUserAnswerResult("Sorry! There was an error.");
		}
	};

	// Set the value of the ArtistOrSongTitle element to the name of the correct answer
	const updateArtistOrSongTitle = (newSong) => {
		// 0 = guess the artist
		if (artistOrSong === 0) {
			setArtistOrSongTitle(newSong.artists[0].name);
		}
		// 1 = guess the song
		else {
			setArtistOrSongTitle(newSong.album.name);
		}
	};

	// Inform the user what to guess this round
	const updateArtistOrSong = (num) => {
		if (num === 0) setArtistOrSong(`Guess the artist!`)
		else setArtistOrSong(`Guess the song!`)
	};

	// Decrease the amount of guesses left
	// Executes only when "Submit Answer" button is clicked
	const updateRemainingGuesses = () => {
		setRemainingGuesses(prevGuesses => prevGuesses - 1);
	};

	// Change the blur depending on how many guesses are left/if the answer was correct
	const updateThumbnailBlur = (isAnswerCorrect) => {
		if (!isAnswerCorrect) {
			switch (remainingGuesses) {
				case 3:
					setThumbnailBlur("blur3")
					break;
				case 2:
					setThumbnailBlur("blur2")
					break;
				case 1:
					setThumbnailBlur("blur1")
					break;
				case 0:
				default:
					setThumbnailBlur("blur0")
					break;
			}
		}
		else setThumbnailBlur("blur0");
	}

	// Update the UI and score if the user guessed correctly
	const correctAnswerRender = () => {
		setUserAnswerResult("Correct!");					// Tell the user they're correct
		setScore(score => score + (remainingGuesses + 1))	// Increase the total score based on how many guesses were left
		setSubmitUserAnswerButtonDisabled(true);			// Disable "submit" button so user goes to next round			
		setStartNewRoundButtonDisabled(false);				// Enable the new round button
	}

	const isUserAnswerCorrect = () => {
		const input = userInput.current.value.trim().toLowerCase()

		// If this is an artist name round
		if (artistOrSong === 0 && song !== null) {
			const correctAnswer = song.artists[0].name.toLowerCase();

			if (correctAnswer === input && input !== "") return true;
			else return false;
		}
		// If this is a song name round
		else if (song !== null) {
			const correctAnswer = song.album.name.toLowerCase()
			
			if (correctAnswer === input && input !== "") return true;
			else return false;
		}
	};

	return (
		<>
			<UpdatePlaylistName playlistYear={playlistYear} />
			<UpdateCurrentRound currentRound={currentRound} playlist={playlist} />
			<UpdateScore score={score} />
			<div className="rounded justify-center">
				<UpdateThumbnail thumbnail={thumbnail} thumbnailBlur={thumbnailBlur} />
				<UpdateArtistOrSongTitle artistOrSongTitle={artistOrSongTitle} />
			</div>
			<UpdateArtistOrSong artistOrSong={artistOrSong} />
			<UpdateRemainingGuesses remainingGuesses={remainingGuesses} />
			<input
				className="shadow appearance-none border rounded w-2/3 mx-auto my-4 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
				id="answer"
				type="text"
				placeholder="Qu'est-ce que c'est!?"
				ref={userInput} />
			<button
				className="bg-white w-1/3 mx-auto my-4 hover:bg-gray-100 disabled:bg-slate-500 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow"
				id="answer-button"
				onClick={(updateRemainingGuesses)}
				disabled={submitUserAnswerButtonDisabled}>
				Submit Answer
			</button>
			<UpdateUserAnswerResult userAnswerResult={userAnswerResult} />
			<button
				className="bg-white w-1/3 mx-auto my-4 hover:bg-gray-100 disabled:bg-slate-500 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow"
				id="next-round-button"
				onClick={() => setStartNewRound(prev => !prev)}
				disabled={startNewRoundButtonDisabled}>
				Next Round
			</button>
		</>
	);
}
