import { useEffect, useRef } from 'react';
import '../../styles/game.css';

export function UpdatePlaylistName({ playlist }) {
	return (
		<p id="playlist-name" className="text-[#ec2a83]">Playlist: <b><i>{playlist.name}</i></b></p>
	);
}

export function UpdateCurrentRound({ currentRound, playlist }) {
	return (
		<p id="round">Ronde {currentRound} / {playlist.tracks.total}</p>
	);
}

export function UpdateScore({ score }) {

	const scoreElement = useRef()
	const scoreIncrease = useRef()

	// Every time the score changes, play an animation
	useEffect(() => {
		scoreElement.current.classList.remove('inflate-animation');
		setTimeout(() => {
			scoreElement.current.classList.add('inflate-animation');
		  }, 10);
		scoreIncrease.current.classList.add('rise-and-fade-animation');
		setTimeout(() => {
			scoreIncrease.current.classList.remove('rise-and-fade-animation');
		  }, 10);
		  
	}, [score])


	return (
		<h3 id="score">Total Score: 
			<span ref={scoreElement}>{score}</span>
			<span ref={scoreIncrease} className='rise-and-fade'>+{score}</span>
		</h3>
	);
}

export function UpdateThumbnail({ thumbnail, thumbnailBlur }) {
	return (
		<div className='py-10'>
			<img
				src={thumbnail}
				className={thumbnailBlur}
				id="thumbnail"
				alt="Thumbnail of a music video."
			/>
		</div>
	);
}

export function UpdateArtistOrSongTitle({ artistOrSongTitle }) {
	return (
		<p id="artistOrSongTitle">{artistOrSongTitle}</p>
	);
}

export function UpdateArtistOrSong({ artistOrSong }) {
	return (
		<p id="artistOrSong"><b className="text-[#ec2a83]">{artistOrSong}</b></p>
	);
}

export function UpdateRemainingGuesses({ remainingGuesses }) {
	return (
		<p id="remaining-guesses">Suppositions restantes: <i className="text-[#ec2a83]">{remainingGuesses}</i></p>
	);
}

export function UpdateUserAnswerResult({ userAnswerResult }) {
	return (
		<p id="result">{userAnswerResult}</p>
	);
}
